mapper.update:
UPDATE
	member m
SET
   m.sum = #{sum} 
WHERE
    m.mid = #{mid}

	UPDATE 
		fund_record
	SET
	  	state = #{state}
   	WHERE
		number = #{number}

mapper.select:
SELECT mid, name, password, phone, address, sum, real_name, bank_password, bank_name, bank_number FROM member WHERE 1 = 1
AND mid = #{mid}
AND name = #{name}

SELECT sn.snid, sn.mid, sn.bet_time, sn.number, sn.type, sn.league, sn.team_h, sn.team_c, sn.bet, sn.score, sn.bet_type, sn.occasion, sn.ior_type, sn.ior_ratio, sn.state, sn.money, sn.valid_money, sn.deal_money, sn.win_lose, m.sum FROM member m RIGHT JOIN single_note sn ON m.mid = sn.mid WHERE 1 = 1
AND sn.state = #{state}
AND sn.bet_time LIKE CONCAT(#{betTime},'%')

SELECT fr.frid, fr.mid, fr.time, fr.number, fr.record, fr.phone_code, fr.money_address, fr.currency, fr.currency_count, fr.money, fr.discounts, fr.state, fr.remark, m.bank_number FROM member m RIGHT JOIN fund_record fr ON m.mid = fr.mid WHERE 1 = 1
AND fr.mid = #{mid}
AND fr.type = #{type}
AND fr.record = #{record}
AND fr.state = #{state}
AND fr.time >= #{beginTime}
AND fr.time <= #{endTime}
order by fr.time desc limit ${beginIndex},${pageSize}

SELECT
	count(*)
FROM
	fund_record
WHERE 1 = 1

	AND mid = #{mid}


	AND type = #{type}


	AND record = #{record}


	AND state = #{state}


	AND time  >=  #{beginTime}


	AND time  <=  #{endTime}

SELECT
	frid,
	mid,
	time,
	number,
	record,
	phone_code,
	money_address,
	currency,
	currency_count,
	money,
	discounts,
	state,
	remark
FROM
	fund_record 
WHERE 1 = 1

	AND mid = #{mid}


	AND type = #{type}


	AND record = #{record}


	AND number = #{number}


	AND state = #{state}


	AND time  >=  #{beginTime}


	AND time  <=  #{endTime}


	order by time desc limit ${beginIndex},${pageSize}

SELECT snid, mid, bet_time, number, type, league, team_h, team_c, bet, score, bet_type, occasion, ior_type, ior_ratio, state, money, valid_money, deal_money, win_lose FROM single_note WHERE 1 = 1
AND snid = #{snid}
AND mid = #{mid}
AND bet_type = #{betType}
AND number = #{number}
AND state = #{state}
AND win_lose = #{winLose}
AND bet_time >= #{beginTime}
AND bet_time <= #{endTime}
AND (league Like CONCAT('%',#{keyword},'%') OR team_h Like CONCAT('%',#{keyword},'%') OR team_c Like CONCAT('%',#{keyword},'%'))
order by bet_time desc limit ${beginIndex},${pageSize}

SELECT
	count(*)
FROM
	single_note 
WHERE 1 = 1

	AND snid = #{snid}


	AND mid = #{mid}


	AND bet_type = #{betType}


	AND number = #{number}


	AND state = #{state}


	AND win_lose = #{winLose}


	AND bet_time  >=  #{beginTime}


	AND bet_time  <=  #{endTime}


	AND (league Like CONCAT('%',#{keyword},'%') 
	OR team_h Like CONCAT('%',#{keyword},'%') 
	OR team_c Like CONCAT('%',#{keyword},'%'))
